import React, { Component } from 'react';
import Person from './components/Persons/Person/Person';
import Persons from '../components/Persons/Persons';




class App extends Component {
  state = {
    persons: [
      {name: 'Max' , age:28},
      {name: 'Manu' , age:29},
      {name: 'Stephanie' , age:26}
    ],
    showPersons: false //Boolean
  }

  togglePersonsHandler = ( event ) => {
    const doesShow = this.state.showPersons;
    this.setState({showPersons: !doesShow})
  }
  nameChangedHandler = (event) => {
    this.setState( { 
      persons: [
        { name: 'Max', age: 28},
        { name: event.target.value, age: 29},
        { name: 'Stephanie',age: 26} 
      ]  
    } ) 
  }
  switchNameHandler = () => { // this is a normal function aka method
    // console.log('was clicked!')
    this.setState( {persons: [ {name: 'Maximillan' , age:28}, {name: 'Manu' , age:29}, {name: 'Stephanie' , age:27} ]} )}


    render() {

    let persons = null;
    let btnClass= '';

    if( this.state.showPersons ) {
      persons = (
        <div>
            <Person persons={this.state.persons}
            clicked ={this.deletePersonHandler}
            changed ={this.nameChangedHandler}/>

      </div>
      );
      btnClass = classes.Red;

    }

    let assignedlasses =[];
    if (this.state.persons.length <= 2) {
      classes.push('red'); // classes = [red]

    }
    if (this.state.persons.length <= 1) {
      classes.push('bold'); // classes = ['red', 'bold']
    }
    return (

      <div className="App App-header">
        <h1> Hi, I'm a React App</h1>
        <p className={btnClass}>This is really working!</p>
        <button 
        className = {btnClass}
        onClick = { this.togglePersonsHandler} className={btnClass}>Toggle Persons</button>
          {persons}

      </div>

    );
  }
}

export default    App;
