import React, { Component } from 'react';
import Person from './Person/Person';
import './App.css';

class App extends Component {
  state = {
    persons: [
      {name: 'Max' , age:28},
      {name: 'Manu' , age:29},
      {name: 'Stephanie' , age:26}
    ],
    showPersons: false //Boolean
  }

  togglePersonsHandler = ( event ) => {
    const doesShow = this.state.showPersons;
    this.setState({showPersons: !doesShow})
  }
  nameChangedHandler = (event) => {
    this.setState( { 
      persons: [
        { name: 'Max', age: 28},
        { name: event.target.value, age: 29},
        { name: 'Stephanie',age: 26} 
      ]  
    } ) 
  }
  switchNameHandler = () => { // this is a normal function aka method
    // console.log('was clicked!')
    this.setState( {persons: [ {name: 'Maximillan' , age:28}, {name: 'Manu' , age:29}, {name: 'Stephanie' , age:27} ]} )}


  render() {
    const style = {
      backgoundColor: 'green',
      color: 'white',
      font: 'inherit',
      border: '1px solid blue',
      padding: '8px',
      cursor: 'pointer'
    };

    let persons = null;
    if( this.state.showPersons ) {
      persons =(
      <div>
        { 
          this.state.persons.map((person,index) => {
          return <Person      
          click={() => this.deletePersonHandler(index)}
          name={person.name}
          age={person.age}
          key={person.id}
          changed={(event) => this.nameChangedHandler(event, person)} /> 
          }) 
        }
      </div>
      )
    }
    return (
      <div className="App App-header">
        <h1> Hi, I'm </h1>
          <button onClick = { this.togglePersonsHandler } >Switch Name</button>

          { this.state.showPersons ? 
          // \         |         /
          // what to do if true case 
          //Ternery expression is used here inside the curly brackets inclosing and will affect the div that has the Person elements
          // QUESTION MARK checks the condition
            <div> 
              <Person name={this.state.persons[0].name} age={this.state.persons[0].age}></Person> 
              <Person name={this.state.persons[1].name} age={this.state.persons[1].age} click={this.switchNameHandler.bind(this, 'Max!')} changed={this.nameChangedHandler}></Person>
              <Person name={this.state.persons[2].name} age={this.state.persons[2].age}></Person>
            </div> : null // colon means else here null means nothing 
          }

      </div>
    );
  }
}

export default App;
