import React, { Component } from 'react';
import Person from './Person/Person';
import './App.css';

class App extends Component {
  state = {
    persons: [
      {name: 'Max' , age:28},
      {name: 'Manu' , age:29},
      {name: 'Stephanie' , age:26}
    ],
    showPersons: false //Boolean
  }

  togglePersonsHandler = ( event ) => {

  }
  nameChangedHandler = (event) => {
    this.setState( { 
      persons: [
        { name: 'Max', age: 28},
        { name: event.target.value, age: 29},
        { name: 'Stephanie',age: 26} 
      ]  
    } ) 
  }
  switchNameHandler = () => { // this is a normal function aka method
    // console.log('was clicked!')
    this.setState( {persons: [ {name: 'Maximillan' , age:28}, {name: 'Manu' , age:29}, {name: 'Stephanie' , age:27} ]} )}
  render() {
    return (
      <div className="App">
          <button onClick = { () => this.togglePersonsHandler } >Switch Name</button>

          { this.state.showPersons ?  //Ternery expression is used here inside the curly brackets inclosing and will affect the div that has the Person elements
            <div> 
              <Person name={this.state.persons[0].name} age={this.state.persons[0].age}></Person> 
              <Person name={this.state.persons[1].name} age={this.state.persons[1].age} click={this.switchNameHandler.bind(this, 'Max!')} changed={this.nameChangedHandler}></Person>
              <Person name={this.state.persons[2].name} age={this.state.persons[2].age}></Person>
            </div> : null // colon means else here null means nothing 
          }

      </div>
    );
  }
}

export default App;
